mapper.select:
SELECT OD.DETAIL_NUM, T.TYPE_NUM, T.TYPE_NAME, I.ITEM_IMG, I.ITEM_NAME, I.ITEM_NUM, C.CUS_NUM, C.CUS_NAME, OD.DETAIL_PRICE, I.PRICE, I.STOCK, OD.ORDER_CNT , O.ORDER_NUM, O.ORDER_DATE, OD.DEPART_TIME, OD.ARRIVE_TIME, O.TOTAL_PRICE, C.CUS_ADDR, C.CUS_TEL, D.DELI_NUM, D.DELI_STATUS, OD.ORDER_NUM FROM cus C, item I, deliver D, order_item O, ORDER_DETAIL OD, item_type T WHERE OD.ORDER_NUM = O.ORDER_NUM AND I.TYPE_NUM = T.TYPE_NUM AND O.CUS_NUM = C.CUS_NUM AND I.ITEM_NUM = OD.ITEM_NUM AND OD.DELI_NUM = D.DELI_NUM AND OD.ORDER_NUM = #{orderNum}
AND ${searchType} LIKE CONCAT('%', #{searchValue}, '%')
AND OD.DELI_NUM IN (1,2,3)
ORDER BY OD.ORDER_NUM
DESC, OD.DETAIL_NUM DESC ${sortValue}, OD.DETAIL_NUM ${sortValue}

SELECT O.ORDER_NUM, C.CUS_NUM, C.CUS_NAME, C.CUS_ADDR, C.CUS_TEL, O.ORDER_DATE, O.TOTAL_PRICE, D.DELI_NUM, D.DELI_STATUS, OD.DETAIL_NUM, T.TYPE_NUM, T.TYPE_NAME, I.ITEM_NUM, I.ITEM_NAME, I.ITEM_IMG, I.PRICE, OD.ORDER_CNT, OD.DETAIL_PRICE, OD.DEPART_TIME, OD.ARRIVE_TIME FROM order_item O JOIN cus C ON O.CUS_NUM = C.CUS_NUM JOIN deliver D ON O.DELI_NUM = D.DELI_NUM JOIN ORDER_DETAIL OD ON O.ORDER_NUM = OD.ORDER_NUM JOIN item I ON OD.ITEM_NUM = I.ITEM_NUM JOIN item_type T ON I.TYPE_NUM = T.TYPE_NUM WHERE O.ORDER_NUM = #{orderNum}
AND ${searchType} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY O.ORDER_NUM ;

SELECT O.ORDER_NUM, C.CUS_NUM, C.CUS_NAME, C.CUS_ADDR, C.CUS_TEL, O.ORDER_DATE, O.TOTAL_PRICE, D.DELI_NUM, D.DELI_STATUS, OD.DETAIL_NUM, T.TYPE_NUM, T.TYPE_NAME, I.ITEM_NUM, I.ITEM_NAME, I.ITEM_IMG, I.PRICE, OD.ORDER_CNT, OD.DETAIL_PRICE, OD.DEPART_TIME, OD.ARRIVE_TIME FROM order_item O JOIN cus C ON O.CUS_NUM = C.CUS_NUM JOIN deliver D ON O.DELI_NUM = D.DELI_NUM JOIN ORDER_DETAIL OD ON O.ORDER_NUM = OD.ORDER_NUM JOIN item I ON OD.ITEM_NUM = I.ITEM_NUM JOIN item_type T ON I.TYPE_NUM = T.TYPE_NUM WHERE O.ORDER_NUM = #{orderNum}
AND ${searchType} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY O.ORDER_NUM;

SELECT ORDER_NUM, ORDER_DATE
FROM order_item
ORDER BY ORDER_NUM ${sortValue};

SELECT COUNT(*)
FROM order_detail
WHERE ORDER_NUM = #{orderNum};

mapper.update:
UPDATE order_item
SET DELI_NUM = 5 
WHERE order_num = #{orderNum};

UPDATE order_detail
SET DELI_NUM = 5
WHERE detail_num = #{detailNum};

UPDATE order_detail OD, item I
SET OD.DELI_NUM = 3, OD.DEPART_TIME = CURRENT_TIMESTAMP, I.STOCK = I.STOCK - OD.ORDER_CNT
WHERE DETAIL_NUM = #{detailNum}
    AND I.ITEM_NUM = OD.ITEM_NUM
    AND I.ITEM_NUM = #{itemNum};

UPDATE order_item O, order_detail OD, item I
SET O.DELI_NUM = 3, OD.DELI_NUM = 3, OD.DEPART_TIME = CURRENT_TIMESTAMP, I.STOCK = I.STOCK - ORDER_CNT
WHERE OD.DETAIL_NUM = #{detailNum}
AND O.ORDER_NUM = OD.ORDER_NUM
AND I.ITEM_NUM = OD.ITEM_NUM
AND OD.ITEM_NUM = #{itemNum};
